package ejercicio1;

public class Ejercicio01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int valor1 = 256;
		int valor2 = 398;
		
		System.out.println("valor1 es: " + valor1 + "valor 2 es: " + valor2);
		System.out.println("su suma es: " + (valor1 + valor2));
		System.out.println("su resta es: " + (valor1 - valor2));
		System.out.println("su producto es: " + (valor1 * valor2));
		System.out.println("su cociente es: " + (valor1 / valor2));
		System.out.println("su modulo es: " + (valor1 % valor2));
	}

}
